            {% set igst_exist = doc.items | sum(attribute="igst_rate") %}
            {% set cgst_exist = doc.items | sum(attribute="cgst_rate") %}
            {% set sgst_exist = doc.items | sum(attribute="sgst_rate") %}

            {% if igst_exist > 0 and cgst_exist == 0 and sgst_exist == 0 %}
                <td width="10%" style="text-align:right;color:white;">IGST<br>
                ({{ frappe.db.get_value("Currency", doc.currency, "symbol") or "" }})</td>
            {% else %}
