                    CGST<br>({{ frappe.db.get_value("Currency", doc.currency, "symbol") or "" }})</td>
                <td width="10%" style="text-align:right; color:white;">
                    SGST<br>({{ frappe.db.get_value("Currency", doc.currency, "symbol") or "" }})</td>
            {% endif %}

            <td width="15%" style="text-align:right;color:white;">Amount<br>
            ({{ frappe.db.get_value("Currency", doc.currency, "symbol") or "" }})</td>
        </tr>
    </thead>

