            <td style="text-align:right;">
            {{ frappe.utils.fmt_money(abs(row.amount), currency=doc.currency) }}</td>

            {% set cgst_amt = 0 %}
            {% set sgst_amt = 0 %}
            {% set igst_amt = 0 %}

            {% if igst_exist > 0 and cgst_exist == 0 and sgst_exist == 0 %}
                {% set igst_amt = (row.amount | float * (row.igst_rate or 0)) / 100 %}
                <td style="text-align:right;">{{ frappe.utils.fmt_money(abs(igst_amt), currency=doc.currency) }}</td>
                {% set total_igst.value = total_igst.value + igst_amt %}
            {% else %}
                {% set cgst_amt = (row.amount | float * (row.cgst_rate or 0)) / 100 %}
                {% set sgst_amt = (row.amount | float * (row.sgst_rate or 0)) / 100 %}
                <td style="text-align:right;">{{ frappe.utils.fmt_money(abs(cgst_amt), currency=doc.currency) }}</td>
                <td style="text-align:right;">{{ frappe.utils.fmt_money(abs(sgst_amt), currency=doc.currency) }}</td>
                {% set total_cgst.value = total_cgst.value + cgst_amt %}
                {% set total_sgst.value = total_sgst.value + sgst_amt %}
            {% endif %}

            <td style="text-align:right;">
                {% set row_amount = (row.amount or 0) + cgst_amt + sgst_amt + igst_amt %}
                {{ frappe.utils.fmt_money(abs(row_amount), currency=doc.currency) }}
                {% set total_amount.value = total_amount.value + row_amount %}
            </td>
        </tr>
