       {# Group items by GST Rate #}
{% set gst_groups = {} %}

{% for i in doc.items %}
    {% set rate = (i.igst_rate or 0) + (i.cgst_rate or 0) + (i.sgst_rate or 0) %}
    {% if gst_groups.update({rate: (gst_groups.get(rate, []) + [i])}) %}{% endif %}
{% endfor %}

{# Sorted GST rates #}
{% set sorted_rates = gst_groups.keys() | list | sort %}

{# Loop over sorted GST groups #}
{% for rate in sorted_rates %}
{% set items = gst_groups[rate] %}
<tr style="font-weight:bold; font-size:11px;">
    <td colspan="5" style="text-align:right;">
        Total @ {{ "{:g}".format(rate) }} %
    </td>


   {% set tax_total = 0 %}

{% if items[0].igst_rate and (items[0].cgst_rate or 0) == 0 and (items[0].sgst_rate or 0) == 0 %}
    {% set tax_total = items | sum(attribute="igst_amount") %}
    <td style="text-align:right;">
        {{ frappe.utils.fmt_money(tax_total, currency=doc.currency) }}
    </td>
    <td></td>
{% else %}
    {% set cgst_total = items | sum(attribute="cgst_amount") %}
    {% set sgst_total = items | sum(attribute="sgst_amount") %}
    {% set tax_total = cgst_total + sgst_total %}
    <td style="text-align:right;">
        {{ frappe.utils.fmt_money(cgst_total, currency=doc.currency) }}
    </td>
    <td style="text-align:right;">
        {{ frappe.utils.fmt_money(sgst_total, currency=doc.currency) }}
    </td>
{% endif %}

<td style="text-align:right; font-weight:bold;">
    {{ frappe.utils.fmt_money( tax_total, currency=doc.currency) }}
</td>

</tr>
{% endfor %}
